class Solution {
    private static final int MOD = 1_000_000_000 + 7;

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        Long[][][] memo = new Long[n][51][51];
        long ans = 0L;
        for (int x = 0; x <= nums[0]; x ++) {
            long val = helper(nums, 1, x, nums[0] - x, memo);
            ans += val;
            ans %= MOD;
        }
        return (int) ans;
    }

    // 
    private long helper(int[] nums, int idx, int x, int y, Long[][][] memo) {
        if (idx == nums.length) {
            return 1L;
        }
        if (memo[idx][x][y] != null) {
            return memo[idx][x][y];
        }
        long ans = 0L;
        for (int xx = x; xx <= nums[idx]; xx ++) {
            int yy = nums[idx] - xx;
            if (yy > y) {
                continue;
            }
            long val = helper(nums, idx + 1, xx, yy, memo);
            ans += val;
            ans %= MOD;
        }
        return memo[idx][x][y] = ans;
    }
}
