class Solution:
    def __init__(self):
        self.mod = 10**9 + 7
        self.n = 0
        self.v = []
        self.dp = []

    def rec(self, i, prev):
        if i == self.n:
            return 1

        if self.dp[i][prev] != -1:
            return self.dp[i][prev]

        ans = 0
        for k in range(prev, self.v[i] + 1):
            if i == 0:
                ans = (ans + self.rec(i + 1, k)) % self.mod
            else:
                if self.v[i - 1] - prev >= self.v[i] - k:
                    ans = (ans + self.rec(i + 1, k)) % self.mod

        self.dp[i][prev] = ans
        return ans

    def countOfPairs(self, nums):
        self.n = len(nums)
        self.v = nums[:]
        maxm = max(nums)
        self.dp = [[-1] * (maxm + 10) for _ in range(self.n)]

        return self.rec(0, 0)
