class Solution {
public:
    int mod = (int)1e9 + 7;
    vector<vector<vector<long long>>> memo;

    long long dfs(vector<int>& a, int a1, int a2, int i) {
        if (i == a.size()) return 1;  // Found a valid pair

        if (memo[i][a1][a2] != -1) return memo[i][a1][a2];

        long long ans = 0;
        for (int x = a1; x <= a[i]; x++) {
            int y = a[i] - x;
            if (y <= a2) {
                ans += dfs(a, x, y, i + 1);
                ans %= mod;
            }
        }

        return memo[i][a1][a2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int maxNum = *max_element(nums.begin(), nums.end());

        memo.resize(n, vector<vector<long long>>(maxNum + 1, vector<long long>(maxNum + 1, -1)));
        return (int)dfs(nums, 0, maxNum, 0);
    }
};
