class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        int dp[2001][51][51];
        memset(dp, -1, sizeof(dp));
        function<int(int, int, int)> rec = [&](int i, int pa, int pb) -> int {
            if (i == n) return 1;
            if (dp[i][pa][pb] != -1) return dp[i][pa][pb];
            int res = 0;
            for (int j = pa; j <= nums[i]; ++j) {
                int cura = j, curb = nums[i] - j;
                if (curb >= 0 && curb <= pb) {
                    res = (1ll * res + rec(i + 1, cura, curb)) % mod;
                }
            }
            return dp[i][pa][pb] = res;
        };
        return rec(0, 0, 50);
    }
};

