class Solution {
public:
    int mod = 1e9 + 7;
    int dp[2001][52][52];
    int f(int i,int s1,int s2,vector<int>&nums)
    {
        if(i >= nums.size()) return 1;
        if(dp[i][s1+1][s2+1] != -1) return dp[i][s1+1][s2+1];
        int ans = 0;
        for(int j=0;j<=nums[i];j++)
        {
            int cnt = 0;
            int a = nums[i] - j;
            int b = nums[i] - a;

            if(s1 == -1 && s2 == -1)
            {
                cnt = (cnt + f(i+1,a,b,nums)) % mod;
            }
            else if(a <= s1 && b >= s2)
            {
                cnt = (cnt + f(i+1,a,b,nums)) % mod;
            }
            ans = (ans + cnt) % mod;
        }
        return dp[i][s1+1][s2+1] = (ans) % mod;
    }
    int countOfPairs(vector<int>& nums) {
        memset(dp,-1,sizeof(dp));
        return f(0,-1,-1,nums);
    }
};
