class Solution {
public:
    int M = 1e9+7;
    int help(vector<int> & nums , int curr , int k , int l , vector<vector<vector<int>>>& dp){
        if(curr >= nums.size()) return 1;
        if(dp[curr][k][l] != -1) return dp[curr][k][l];
        int ans = 0;
        for(int i = k ; i <= nums[curr] ; i++){
            for(int j = l ; j >= 0 ; j--){
                if(i+j == nums[curr]){
                    ans = (ans%M + help(nums , curr+1 , i , j , dp)%M)%M;
                }
            }
        }
        return dp[curr][k][l] =  ans;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>> dp(nums.size() , vector<vector<int>>(51 , vector<int>(51 , -1)));
        return help(nums , 0 , 0 , nums[0] , dp);
    }
};
