class Solution {
public:
int mod=1e9+7;
int n;
vector<int>nums;
int dp[2001][52][52];
    int countpossible(int ind,int ge,int le)
    {
        if(ind>=n)
            return 1;

        if(dp[ind][ge][le]!=-1)
        return dp[ind][ge][le];
        int x=nums[ind];
        int ans=0;
        for(int i=ge;i<=x;i++)
        {
            int fe=i;
            int se=x-i;

            if(se>le)
            {
                continue;
            }
            else
            {
                ans+=countpossible(ind+1,max(ge,fe),min(le,se));
                ans=ans%mod;
            }
        }
        dp[ind][ge][le] = ans%mod;
        return dp[ind][ge][le];
    }
    int countOfPairs(vector<int>& arr) {
        n=arr.size();
        nums=arr;
        for(int i=0;i<=n;i++)
        {
            for(int j=0;j<=50;j++)
            {
                for(int k=0;k<=50;k++)
                {
                    dp[i][j][k]=-1;
                }
            }
        }
        return countpossible(0,0,50);
    }
};
