#define ll long long int
class Solution {
public:
ll mod = 1e9 + 7;
    ll solve(ll ind, ll i1, ll i2, vector<int>& nums, vector<vector<vector<ll>>>& dp) {
        ll n = nums.size();
        if (ind >= n) return 1;
        ll ans = 0;
        if (dp[ind][i1][i2] != -1)
        {
            return dp[ind][i1][i2];
        }
        for (int i = i1; i <= nums[ind]; i++)
        {
            for (int j = i2; j >= 0; j--)
            {
                if (i + j == nums[ind])
                {
                    ans = (ans + solve(ind + 1, i, j, nums, dp))%mod;
                }
            }
        }
        return dp[ind][i1][i2] = ans%mod;
    }

    ll countOfPairs(vector<int>& nums)
    {
        ll n = nums.size();
        vector<vector<vector<ll>>> dp(n, vector<vector<ll>>(51, vector<ll>(51, -1)));
        return solve(0, 0, nums[0], nums, dp);
    }
};
