class Solution {
public:
    long dp[5001][101];
    long m=1e9+7;
    long ways(vector<int> &nums,long ind,long px){
       long n=nums.size();
       if(ind==n) return 1;
       if(dp[ind][px]!=-1) return dp[ind][px];
       long py=nums[ind-1]-px,ans=0;
       for(long i=0;i<=nums[ind];i++){
            long j=nums[ind]-i;
            if(px<=i && py>=j) ans=(ans%m+0LL+ways(nums,ind+1,i)%m)%m;
       }
       return dp[ind][px]=ans%m;
    }
    int countOfPairs(vector<int>& nums) {
        long ans=0,n=nums.size();
        memset(dp,-1,sizeof dp);
        for(int i=0;i<=nums[0];i++) ans+=ways(nums,1,i),ans%=m;
        return ans;
    }
};
