class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<long long>> f;
        vector<long long> tmp;
        int MAXMAX = 1000000007;

        for (int i = 0; i <= 50; i++) {
            tmp.push_back(0);
        }
        for (int i = 0; i < nums.size(); i++) {
            f.push_back(tmp);
        }

        for (int num = 0; num <= nums[nums.size() - 1]; num++) {
            f[nums.size() - 1][num] = 1;
        }
        for (int i = nums.size() - 2; i >= 0; i--) {

            for (int num = nums[i]; num >= 0; num--) {

                int lower_bound_next_num1 = max(num, nums[i+1] - nums[i] + num), upper_bound_next_num1 = nums[i+1];
                long long acc = 0;
                for (int next_num1 = lower_bound_next_num1; next_num1 <= upper_bound_next_num1; next_num1++) {
                    acc += f[i+1][next_num1];
                    if (acc >= MAXMAX) {
                        acc = acc % MAXMAX;
                    }
                }

                f[i][num] = acc % MAXMAX;
            }
        }
        long long ans = 0;
        for (int num = 0; num <= nums[0]; num++) {
            ans += f[0][num];
            if (ans >= MAXMAX) {
                ans = ans % MAXMAX;
            }
        }
        return ans;
    }
};
