class Solution {
public:

    int MOD = 1e9+7;

    long long findp(vector<int>& nums, int pre, vector<vector<long long>>&dp, int id){
        if(id==nums.size()){
            return 1;
        }
        if(dp[id][pre]!=-1){
            return dp[id][pre];
        }
        long long temp = 0;
        for(int i = pre; i<=nums[id]; i++){
            if(nums[id]-i<=nums[id-1]-pre){
                temp+=findp(nums,i,dp,id+1);
                temp%=MOD;
            }
        }
        return dp[id][pre] = temp;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        vector<vector<long long>> dp(n,vector<long long>(50+1,-1));
        long long out = 0;
        for(int i = 0; i<=nums[0]; i++){
            out+=findp(nums,i,dp,1);
            out%=MOD;
        }
        return out;
    }
};
