class Solution {
    int soln(int a,int p,vector<int>&nums,vector<vector<int>>&dp){
        if(a>=nums.size())return 1;
        if(dp[a][p]!=-1)return dp[a][p];
        long long int sum =0;
        long long int val=0;
        int d;
        if(a==0){
            d=100;
        }else{
            d=nums[a-1]-p;
        }
        int s = max(p,nums[a]-d);
        for(int i=s;i<=nums[a];i++){
            val=soln(a+1,i,nums,dp);
            sum = ((sum%mod)+(val%mod))%mod;
        }
        return dp[a][p]=(sum%mod);
    }
public:
    int mod = 1e9 +7;
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(2002,vector<int>(53,-1));
        return soln(0,0,nums,dp);
        // return ans;
    }
};
