class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9 + 7

        dp = [1] * (nums[0] + 1) + [0] * (50 - nums[0])

        for x, prev in zip(nums[1:], nums):
            temp = [0] * 51
            for y in range(51):
                yr = x - y
                for z in range(51):
                    zr = prev - z

                    if y <= z and yr >= zr and yr >= 0 and zr >= 0:
                        temp[y] += dp[z]

            dp = [v % mod for v in temp]

        return sum(dp) % mod
