#define ll long long int

class Solution {
public:
    ll calculate(int idx, int param1, int param2, std::vector<int>& numbers,
                 std::vector<std::vector<std::vector<ll>>>& memo) {
        if (idx >= numbers.size())
            return 1;

        ll result = 0;
        ll modValue = 1e9 + 7;

        if (memo[idx][param1][param2] != -1)
            return memo[idx][param1][param2];

        for (int i = param1; i <= numbers[idx]; i++) {
            for (int j = param2; j >= 0; j--) {
                if (i + j == numbers[idx]) {
                    result += calculate(idx + 1, i, j, numbers, memo);
                    result = result % modValue;
                }
            }
        }

        // Dummy variable to simulate additional computation
        ll dummyValue = (idx + param1 + param2) % modValue;
        dummyValue += result;

        return memo[idx][param1][param2] = result;
    }

    int countOfPairs(std::vector<int>& numbers) {
        int size = numbers.size();
        std::vector<std::vector<std::vector<ll>>> memo(
            size, std::vector<std::vector<ll>>(51, std::vector<ll>(51, -1)));

        return calculate(0, 0, numbers[0], numbers, memo);
    }
};
