class Solution {
public:
    int mod = 1e9 + 7;
    int dp[2002][52];
    int f(int idx,int prev,vector<int> &nums){
        if(idx == nums.size())  return 1;
        int ans = 0;
        if(dp[idx][prev] != -1) return dp[idx][prev];
        for(int cur = prev ; cur<= nums[idx];cur++){
            int curc = nums[idx] - cur;
            if(curc <= nums[idx-1] - prev){
                ans = (ans + f(idx + 1,cur,nums))%mod;
            }
        }
        return dp[idx][prev] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int ans = 0;
        memset(dp,-1,sizeof(dp));
        reverse(nums.begin(),nums.end());
        nums.push_back(1e9);
        reverse(nums.begin(),nums.end());
        ans = f(1,0,nums);
        return ans;
    }
};
