class Solution {
    int MOD = (int) 1e9 + 7;
    Integer dp[][][];

    public int countOfPairs(int[] a) {
        int ans = 0;
        int n = a.length;
        dp = new Integer[n][51][51];
        ans = f(0, 0, 50, n, a);
        return ans;
    }

    int f(int i, int n1, int n2, int n, int a[]) {
        if (i >= n)
            return 1;
        if (dp[i][n1][n2] != null)
            return dp[i][n1][n2];
        long cnt = 0;
        for (int k = n1; k <= a[i]; k++) {
            int l = a[i] - k;
            if (l <= n2) {
                cnt = (cnt + f(i + 1, k, l, n, a)) % MOD;
            }
        }
        return dp[i][n1][n2] = (int) cnt;
    }
}
