class Solution {
public:
    long long mod=1e9+7;

    long long ans(int i,int p,vector<int>& v,vector<vector<long long>>& dp)
    {   
        if(i==v.size()) return 1;
        if(dp[i][p]!=-1) return dp[i][p];
        long long c=0;
        for(int j=0;j<=v[i];j++)
        {
            if(j>=p&&(v[i]-j)<=(v[i-1]-p)) c=(c+ans(i+1,j,v,dp))%mod;
        }
        return dp[i][p]=c;
    }

    int countOfPairs(vector<int>& nums) {
        int c=0;
        vector<vector<long long>>dp(nums.size(),vector<long long>(1001,-1));
        for(int i=0;i<=nums[0];i++)
        {
            c=(c+ans(1,i,nums,dp))%mod;
        }
        return c;
    }
};
