class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9 + 7

        @cache
        def dp(i, x, y):
            if i == len(nums): return 1

            res, current_num = 0, nums[i]

            for x1 in range(51):
                y1 = current_num - x1
                if x1 < 0 or y1 < 0 or x1 > 50 or y1 > 50: continue
                if x1 < x or y1 > y: continue
                res += dp(i+1, x1, y1)
            return res

        return dp(0, -1, math.inf) % MOD
