#define MOD 1000000007
class Solution {
public:

    int solve(vector<int>& nums, int ind, int prev, int prevv, vector<vector<vector<int>>>& dp) {
        if (ind == nums.size()) {
            return 1;
        }

        if (dp[ind][prev + 1][prevv] != -1) {
            return dp[ind][prev + 1][prevv];
        }

        int ans = 0;

        for (int i = 0; i <= nums[ind]; i++) {
            if (i >= prev && nums[ind] - i <= prevv) {
                ans = (ans + solve(nums, ind + 1, i, nums[ind] - i,dp)) % MOD;
            }
        }

        return dp[ind][prev + 1][prevv] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>> dp(nums.size(),vector<vector<int>>(52,vector<int>(52,-1)));
        return solve(nums, 0, -1, 51,dp);
    }
};
