class Solution {
    int mod=(int)1e9+7;
    int helper(Integer dp[][][],int i,int prev1,int prev2,int nums[]){
        if(i==nums.length) return 1;
        if(dp[i][prev1+1][prev2+1]!=null) return dp[i][prev1+1][prev2+1];
        int ans=0;
        for(int j=0;j<=nums[i];j++){
            if((prev1==-1 || j>=prev1) && (prev2==-1 || nums[i]-j<=prev2)){
                ans=(helper(dp,i+1,j,nums[i]-j,nums)+ans)%mod;
            }
        }
        return dp[i][prev1+1][prev2+1]=ans;
    }
    public int countOfPairs(int[] nums) {
        int n=nums.length;
        Integer dp[][][]=new Integer[n][52][52];
        return helper(dp,0,-1,-1,nums);
    }
}
