class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10**9 + 7

        @cache
        def backtrack(i, last1, last2):
            if i == n:
                return 1

            res = 0
            num = nums[i]
            for j in range(0, num+1):
                if last1 <= j and last2 >= num - j:
                    res += backtrack(i+1, j, num - j)
            return res % MOD

        return backtrack(0, -inf, inf) % MOD
