static int F[2'000][51];
static int const MOD = 1'000'000'007;
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int const N = nums.size();
        int const M = *max_element(nums.begin(), nums.end());
        long long result = 0;
        memset(F, 0, sizeof(F));
        for (int j = 0; j <= M; ++j)
            F[0][j] = 1;
        for (int i = 1; i < N; ++i)
            for (int j = 0; j <= M; ++j)
                if (F[i - 1][j])
                {
                    int const k = nums[i - 1] - j;
                    for (int nj = j, nk = nums[i] - nj; nj <= M && nk >= 0; ++nj, --nk)
                        if (nk <= k)
                        {
                            F[i][nj] += F[i - 1][j];
                            if (F[i][nj] >= MOD)
                                F[i][nj] -= MOD;
                        }
                }
        for (int j = 0; j <= M; ++j)
            result += F[N - 1][j];
        return result % MOD;
    }
};
