class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        const int MOD = 1e9 + 7;

        vector<vector<int>> dp(n, vector<int>(201, 0));

        // Initialize dp for the first element
        for (int i = 0; i <= nums[0]; ++i) {
            dp[0][i] = 1; // arr1[0] = i, arr2[0] = nums[0] - i
        }

        // Fill the dp table
        for (int i = 1; i < n; ++i) {
            vector<int> new_dp(201, 0);
            for (int x = 0; x <= nums[i]; ++x) {
                int y = nums[i] - x;
                for (int prev_x = 0; prev_x <= x; ++prev_x) {
                    if (y <= nums[i - 1] - prev_x) {
                        new_dp[x] = (new_dp[x] + dp[i - 1][prev_x]) % MOD;
                    }
                }
            }
            dp[i] = new_dp;
        }

        // Sum all valid sequences for the last element
        int result = 0;
        for (int i = 0; i <= nums[n - 1]; ++i) {
            result = (result + dp[n - 1][i]) % MOD;
        }

        return result;
    }
};
