class Solution {
    int MOD = (int)1e9 + 7;
    int solve(int[] nums, int ind, int last, int[][] dp) {

        if (ind == nums.length) return 1;

        if (dp[ind][last+1] != -1) return dp[ind][last+1];

        int ans = 0;

        for (int i=0;i<=nums[ind];i++) {
            if (ind == 0) {
                ans = (ans + solve(nums, ind+1, i, dp))%MOD;
            } else {
                if (i >= last && nums[ind-1]-last >= nums[ind]-i) {
                    ans = (ans + solve(nums, ind+1, i, dp))%MOD;
                }
            }
        }

        return dp[ind][last+1] = ans;

    }

    public int countOfPairs(int[] nums) {
        int[][] dp = new int[2001][52];
        for (int[] it : dp) Arrays.fill(it, -1);
        return solve(nums, 0, -1, dp);
    }
}
