impl Solution {
    pub fn count_of_pairs(nums: Vec<i32>) -> i32 {
        const MOD_VAL: i32 = 1_000_000_007;

        let max_value = *nums.iter().max().unwrap_or(&0);
        let size = nums.len();


        let mut memo = vec![vec![vec![-1; (max_value + 1) as usize]; (max_value + 1) as usize]; size];

        fn count_pairs(index: usize, min_val: i32, max_val: i32, nums: &[i32], memo: &mut Vec<Vec<Vec<i32>>>) -> i32 {
            if index >= nums.len() {
                return 1;
            }
            if memo[index][min_val as usize][max_val as usize] != -1 {
                return memo[index][min_val as usize][max_val as usize];
            }

            let mut result = 0;
            for i in min_val..=nums[index] {
                for j in 0..=max_val {
                    if i + j == nums[index] {
                        result = (result + count_pairs(index + 1, i, j, nums, memo)) % MOD_VAL;
                    }
                }
            }

            memo[index][min_val as usize][max_val as usize] = result;
            result
        }

        count_pairs(0, 0, nums[0], &nums, &mut memo)
    }
}
