const int M=1e9+7;
class Solution {
public:
    vector<int>v;
    vector<vector<vector<int>>>dp;
    int fun(int i,int p,int pp){
        int n=v.size();
        if(i==n){
            return 1;
        }
        int &c=dp[i][p][pp];
        if(c!=-1) return c;
        int ans=0;
        if(p==51){
            for(int j=0;j<=v[i];j++){
                ans+=fun(i+1,j,v[i]-j)%M;
                ans%=M;
            }
        }
        else {
            for(int j=0;j<=v[i];j++){
                if(p<=j && (v[i]-j)<=(pp)){
                    ans+=fun(i+1,j,(v[i]-j))%M;
                    ans%=M;
                }
            }
        }
        return c=ans;
    }
    int countOfPairs(vector<int>& nums) {
        v=nums;
        int n=nums.size();
        dp.clear();
        dp.resize(n+1,vector<vector<int>>(52,vector<int>(52,-1)));
        return fun(0,51,51);
    }
};
