class Solution {
    public int countOfPairs(int[] nums) {
       int n = nums.length;
        int m = 1000000007;


        int[][] dp = new int[n + 1][1001];


        for (int j = 0; j <= nums[0]; j++) {
            dp[1][j] = 1;
        }


        for (int i = 2; i <= n; i++) {
            int[] curr = new int[1001];
            for (int j = 0; j <= nums[i - 1]; j++) {
                for (int k = 0; k <= j; k++) {
                    int t = nums[i - 1] - j;
                    if (t >= 0 && t <= nums[i - 2] - k) {
                        curr[j] = (curr[j] + dp[i - 1][k]) % m;
                    }
                }
            }
            dp[i] = curr;
        }


        int ans = 0;
        for (int j = 0; j <= nums[n - 1]; j++) {
            ans = (ans + dp[n][j]) % m;
        }

        return ans;


    }
}
