class Solution {
public:

    int mod = 1e9+7;

    int dp[2000][51];

    int help(int idx, int p, vector<int>& nums)
    {
        if (idx == nums.size())
            return 1;

        if (p > nums[idx])
            return 0;

        if (dp[idx][p] != -1)
            return dp[idx][p];

        int res = 0;

        for(int i = p; i <= nums[idx]; i++)
        {
            if (idx == 0 || nums[idx-1] - p >= nums[idx] - i)
                res = (res + help(idx+1, i, nums))%mod;
        }

        return dp[idx][p] = res;
    }

    int countOfPairs(vector<int>& nums) {
        fill_n(dp[0], 2000*51, -1);   
        return help(0, 0, nums);
    }
};
