class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        max_num = max(nums)

        memo = [0] * (max_num + 1)
        for i in range(nums[0] + 1):
            memo[i] = 1 

        for i in range(1, n):
            tmp = [0] * (max_num + 1)
            for j in range(nums[i] + 1):
                for k in range(j + 1):  
                    if nums[i] - j <= nums[i-1] - k:  
                        tmp[j] = (tmp[j] + memo[k]) % (10**9 + 7)
            memo = tmp

        return sum(memo) % (10**9 + 7)
