class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10**9 + 7
        @cache
        def dp(i, p1, p2):
            if i == n:
                return 1
            cnt = 0
            x = nums[i]+1
            for j in range(nums[i]+1):
                if j >= p1 and x - j <= p2:
                    cnt += dp(i + 1, j, x - j)
            return cnt % MOD
        return dp(0, 0, 51)
