class Solution {
public:
    int mod=1e9+7;
    int solve(int prev,int i,vector<int>& nums,int n,vector<vector<int>>& dp){
        if(i==n) return 1;
        if(dp[prev][i]!=-1) return dp[prev][i]%mod;
        int ans=0;
        for(int j=0;j<=nums[i];j++){
            int a1=j,a2=nums[i]-j;
            if(i==0 || (a1>=prev && a2<=(nums[i-1]-prev))){
                ans=((ans%mod)+(solve(a1,i+1,nums,n,dp)%mod))%mod;
            }
        }
        return dp[prev][i]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int>> dp(52,vector<int>(n,-1));
        return solve(51,0,nums,n,dp);
    }
};
