class Solution {
public:
    int mod=1e9+7;
    vector<vector<int>>v;
    long long helper(int prv,int i, vector<int>&nums,vector<vector<long long>>&dp){
        if(i>=nums.size()){
            // v.push_back(num);
            return 1;
        }
        if(dp[prv][i]!=-1) return dp[prv][i];
        int mini=prv;
        int maxi=min(nums[i]-prv,nums[i-1]-prv);
        long long ans=0;
        // cout<<i<<" "<<prv<<" "<<mini<<" "<<maxi<<endl;
        int prvnum=(nums[i-1]-prv);
        for(int j=prv;j<=nums[i];j++){
            if((nums[i]-j)<=prvnum)  ans=(ans+helper(j,i+1,nums,dp)%mod)%mod;
            // num.push_back(j);

            // num.pop_back();
        }
        return dp[prv][i]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int maxi=0;
        for(int i=0;i<nums.size();i++){
            maxi=max(maxi,nums[i]);
        }
        vector<vector<long long>>dp(maxi+1,vector<long long>(nums.size()+1,-1));
        long long ans=0;
        for(int i=0;i<=nums[0];i++){
            ans=(ans+helper(i,1,nums,dp)%mod)%mod;
        }
        // for(auto each: v){
        //     for(int i=0;i<each.size();i++){
        //         cout<<each[i]<<" ";
        //     }
        //     cout<<endl;
        // }
        return ans;
    }
};
