typedef long long ll;

class Solution {
public:
    int mod = 1e9 + 7;
    vector<vector<ll>> dp;

    int solve(int i, int last, int last1, vector<int>& nums) {
        if (i == nums.size()) return 1;
        if (dp[i][last] != -1) return dp[i][last];

        ll res = 0; 
        for (int j = last; j <= nums[i]; ++j) {
            if (j >= last && nums[i] - j <= last1) {
                res += ll(solve(i + 1, j, nums[i] - j, nums)) % mod; 
            }
        }

        return dp[i][last] = res%mod;
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<ll>>(n, vector<ll>(51, -1));
        return solve(0, 0, 50, nums);
    }
};
