using ll = long long;
class Solution {
public:
    int countOfPairs(vector<int>& A) {
        ll dp[2001][51];
        ll MOD = 1e9 + 7;
        memset(dp, -1, sizeof(dp));
        int n = A.size();
        std::function<ll(int, int)> dfs = [&](int i, int j) {
            if (i == n - 1) return 1LL;
            if (dp[i][j] != -1) return 1LL * dp[i][j];

            ll res = 0;
            for (int k = 0; k <= A[i + 1]; ++k) {
                if ((A[i] - j >= A[i + 1] - k) && k >= j) {
                    res += dfs(i + 1, k);
                    res %= MOD;
                }
            }


            dp[i][j] = res % MOD;
            return dp[i][j];
        };
        ll ret = 0;
        for (int i = 0; i <= A[0]; ++i) {
            ret += dfs(0, i);
            ret %= MOD;
        }
        return ret;
    }
};
