class Solution {
public:
    const int MOD = 1e9 + 7;

    vector<vector<vector<int>>> dp;

    int countPairs(int i, int px, int py, const vector<int>& nums) {
        int n = nums.size();

        if (i == n) {

            return 1;
        }

        if (dp[i][px][py] != -1) {
            return dp[i][px][py];
        }

        int ans = 0;

        for (int x = 0; x <= nums[i]; ++x) {
            int y = nums[i] - x;
            if (y >= 0 && x >= px && y <= py) {
                ans = (ans + countPairs(i + 1, x, y, nums)) % MOD;
            }
        }

        return dp[i][px][py] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        dp = vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
        return countPairs(0, 0, 50, nums);
    }
};
