class Solution:
    def countOfPairs(self, nums: List[int]) -> int:

        leng = len(nums)
        mod = 10**9 + 7

        ### incr arr
        dp = defaultdict(int)

        for i in range(nums[0] + 1):
            dp[i] = 1

        for i in range(1, leng):

            pv = nums[i-1]
            cv = nums[i]
            tmp = defaultdict(int)

            for k,v in dp.items():

                monod = pv - k
                for nv in range(cv + 1):
                    if nv < k:
                        continue
                    if cv - nv > monod:
                        continue
                    tmp[nv] += v

            dp = deepcopy(tmp)

        return sum(dp.values()) % mod
