class Solution {
    int mod = (int)(1e9) + 7;
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        int max = 0;
        for(int d: nums) {
            max = Math.max(max, d);
        }

        long[][] dp = new long[n][max+1];
        for(int i  = 0;i<n;i++) {
            Arrays.fill(dp[i], -1);
        }

        long ans = 0;
        for(int j = 0;j<=Math.min(max, nums[0]);j++) {
            ans += rec(0,j, nums[0] - j, dp, nums,n);
            ans = ans%mod;
        }
        return (int)ans;
    }

    public long rec(int i, int val, int val2, long[][] dp, int[] nums, int n) {
        if(i == n-1) {
            return 1; // reached all
        }

        if(dp[i][val] != -1) return dp[i][val];

        long res = 0;
        for(int k = val;k<=nums[i+1];k++) {
            int next2 = nums[i+1] -k;
            if(next2<= val2) {
                res += rec(i+1, k, next2, dp, nums,n);
                res = res%mod;
            }
        }
        dp[i][val] = res;
        return res;
    }
}
