class Solution {
public:
int mod=1e9+7;
    int helper(int ind,int pre,vector<int>&nums,vector<vector<int>>&dp){
        if(ind>=nums.size()){
            return 1;
        }
        if(dp[ind][pre+1]!=-1){
            return dp[ind][pre+1];
        }
        int ans=0;
        if(pre==-1){
            for(int i=0;i<=nums[ind];i++){
                ans=(ans+helper(ind+1,i,nums,dp))%mod;
            }
        }else{
            for(int i=pre;i<=nums[ind];i++){
                if(i>=pre && nums[ind-1]-pre>=nums[ind]-i){
                ans=(ans+helper(ind+1,i,nums,dp))%mod;}
            }
        }

        return dp[ind][pre+1]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>>dp(nums.size(),vector<int>(52,-1));
        return helper(0,-1,nums,dp);
    }
};
