#define ll long long
class Solution {
public:
    int countOfPairs(vector<int>& nums){
        ll n = nums.size(), mod = 1e9+7;
        vector<vector<ll>> dp(n, vector<ll> (51, 0));

        for(int j=0; j<=nums[0]; j++){
            dp[0][j] = 1;
        }

        for(int i=1; i<n; i++){
            for(int j=0; j<=nums[i]; j++){
                for(int k=0; k<=j; k++){
                    if(nums[i-1]-k >= 0 && nums[i]-j >= 0 && nums[i-1]-k >= nums[i]-j)
                        dp[i][j] = (dp[i][j] + dp[i-1][k]) % mod;
                }
            }
        }

        ll ans = 0;
        for(int j=0; j<=50; j++)
            ans = (ans + dp[n-1][j]) % mod;

        return ans;
    }
};
