class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9+7
        ans = 0
        @cache
        def dp(i, val1, val2): # at least val1 for x1, at most val2 for x2
            if i == len(nums):
                return 1
            if val1 > nums[i]:
                return 0
            res = 0
            for x1 in range(val1, nums[i]+1):
                if nums[i]-x1 > val2: continue
                # if i == 1 and val1 == 0 and val2 == 3:
                    # print("x")
                res += dp(i+1, x1, nums[i]-x1)
            # print(i, val1, val2, res)
            return res%MOD
        for i in range(nums[0]+1):
            # print(i, dp(0, i, nums[0]-i))
            ans += dp(1, i, nums[0]-i)
        return ans%MOD
