class Solution {
    final int MOD=1000000007;
    public int countOfPairs(int[] nums) {
        int n=nums.length;
        int[][][] dp=new int[n][51][51];
        for(int[][] arr:dp) {
            for(int[] arr2:arr) Arrays.fill(arr2,-1);
        }
        int ans=0;
        for(int i=0;i<=nums[0];i++) {
            ans=(ans+countOfPairs(nums,dp,1,i,nums[0]-i))%MOD;
        }
        return ans;
    }
    int countOfPairs(int[] nums,int[][][] dp,int i,int num1,int num2) {
        if(i==nums.length)  return 1;
        if(dp[i][num1][num2]!=-1)   return dp[i][num1][num2];
        int count=0;
        for(int j=num1;j<=nums[i];j++) {
            if(nums[i]-j<=num2) {
                count=(count+countOfPairs(nums,dp,i+1,j,nums[i]-j))%MOD;
            }
        }
        return dp[i][num1][num2]=count;
    }
}
