class Solution {
public:
    int dp[2001][51];
    int m=1e9+7,n;
    vector<int> nums;
    long long int f(int i,int j){
        if(i==n)return 1;
        if(dp[i][j]!=-1)return dp[i][j];
        dp[i][j]=0;
        if(i){
            int k=i-1;
            for(int x=max(j,nums[i]-nums[k]+j),y=0;x<=nums[i];x++){
                // y=nums[i]-x;
                dp[i][j] = (dp[i][j] + f(i+1,x))%m;
            }
        }
        else{
            for(int x=0,y=0;x<=nums[i];x++){
                dp[i][j] = (dp[i][j] + f(i+1,x))%m;
            }
        }
        return dp[i][j];
    }
    int countOfPairs(vector<int>& num) {
        memset(dp,-1,sizeof dp);
        nums=num;
        n=nums.size();
        return f(0,0);
    }
};
