class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n=nums.size(),mod=1e9+7;
        vector<vector<vector<long long>>> dp(n, vector<vector<long long>>(51,vector<long long>(51,0)));
        for(int i=0;i<=50;++i){
            for(int j=0;j<=50;++j){
                if(i+j==nums[n-1]){
                    dp[n-1][i][j]++;
                }
            }
        }
        for(int i=n-2;i>=0;--i){
            vector<long long> suff(51);
            long long suff2=0;
            for(int j=50;j>=0;--j){
                suff2=0;
                for(int k=0;k<=50;++k){
                    suff[k]+=dp[i+1][j][k];
                    suff[k]%=mod;
                    suff2+=suff[k];
                    suff2%=mod;
                    if(j+k==nums[i]){
                        dp[i][j][k]+=suff2;
                        dp[i][j][k]%=mod;
                    }
                }
            }
        }
        long long ans=0;
        for(int i=0;i<=50;++i){
            for(int j=0;j<=50;++j){
                if(i+j==nums[0]){
                    ans+=dp[0][i][j]%mod;
                    ans%=mod;
                }
            }
        }
        return ans;
    }
};
