class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        def modsum(aa):
            res=0
            for a in aa:
                res+=a
                res%=md
            return res
        md=10**9+7
        m=max(nums)
        dp=[0]*(m+1)
        dp[0]=1
        ps=1000
        for s in nums:
            cs=[0]
            for v in dp:cs.append((cs[-1]+v)%md)
            dp = [0]*(m+1)
            for a in range(s+1):
                r=max(0,min(a+1,ps-s+a+1))
                dp[a]=cs[r]
            ps=s
            # print(s,dp)
        return modsum(dp)
