class Solution:
	def countOfPairs(self, nums: List[int]) -> int:
		self.mod = 10**9 + 7
		dp = {}
		def rec(level, prev_arr1_val, prev_arr2_val):
			if level == len(nums):
				return 1
			if (level, prev_arr1_val, prev_arr2_val) in dp:
				return dp[(level, prev_arr1_val, prev_arr2_val)]
			count = 0
			for i in range(prev_arr1_val, nums[level] + 1):
				val1, val2 = i, nums[level] - i
				if val2 <= prev_arr2_val:
					count = (count + rec(level + 1, val1, val2)) % self.mod
			dp[(level, prev_arr1_val, prev_arr2_val)] = count
			return count
		return rec(0, 0, float('inf'))
