class Solution:

    def _countOfPairs(self, counts: Dict[Tuple[int, int], int], num: int) -> Dict[Tuple[int, int], int]:
        newCounts: Dict[Tuple[int, int], int] = {}
        for (lower, upper), count in counts.items():
            for i in range(lower, num + 1):
                j = num - i
                if j > upper:
                    continue
                newCounts[(i, j)] = newCounts.get((i, j), 0) + count

        return newCounts

    def countOfPairs(self, nums: List[int]) -> int:
        counts: Dict[Tuple[int, int], int] = {(0, 50): 1}
        for num in nums:
            counts = self._countOfPairs(counts, num)

        return sum(counts.values()) % (10**9 + 7)
