class Solution {
public:
    int mod=1e9+7;
    int dp[2001][52][52];
    int solve(vector<int>& nums,int idx,int prev1,int prev2){
        if(idx>=nums.size())return 1;
        if(dp[idx][prev1][prev2]!=-1)return dp[idx][prev1][prev2];
        int ways=0;

        for(int i=0;i<=nums[idx];i++){
            int curr1=i;
            int curr2=nums[idx]-i;

            if(curr1>=prev1 && curr2<=prev2){
                ways=(ways+solve(nums,idx+1,curr1,curr2))%mod;
            }


        }
        return dp[idx][prev1][prev2]= ways%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int prev1=0;
        int prev2=51;
        for(int i=0;i<nums.size()+1;i++){
            for(int j=0;j<52;j++){
                for(int k=0;k<52;k++){
                    dp[i][j][k]=-1;
                }
            }
        }

        return solve(nums,0,prev1,prev2);
    }
};
