class Solution {
public:
    int dp[2006][53][53]; 
    long long int mod = 1e9 +7;
    long long int solve(int idx, vector<int>& nums, int prev1, int prev2) {
        // Base case
        if (idx >= nums.size()) {
            return 1;
        }


        if (dp[idx][prev1 + 1][prev2] != -1) {
            return dp[idx][prev1 + 1][prev2];
        }

        int total = 0;


        for (int i = 0; i <= nums[idx]; ++i) {
            int first = i;
            int second = nums[idx] - i;
            if (first >= prev1 && second <= prev2) {
                total = (total+ solve(idx + 1, nums, first, second))%mod;
            }
        }


        return dp[idx][prev1 + 1][prev2] = total;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));  
        return solve(0, nums, -1, 50);  
    }
};
