class Solution {
public:
    int m=1e9+7;
    int f(int ind, int prev, int pDown, vector<int> &nums,
    vector<vector<vector<int>>> &dp){
        if(ind==nums.size()) return 1;
        if(dp[ind][prev][pDown]!=-1) return dp[ind][prev][pDown];
        int ct=0;
        for(int i=0; i<=nums[ind]; i++){
            int curr=i;
            int cDown=nums[ind]-i;
            if(curr>=prev && cDown<=pDown){
                ct = ((ct%m)+(f(ind+1,curr,cDown,nums,dp)%m))%m;
            }
        }
        return dp[ind][prev][pDown]=ct;
    }
    int countOfPairs(vector<int>& nums) {

        int n=nums.size();
        vector<vector<vector<int>>> dp(n,vector<vector<int>>(52, vector<int>(52,-1)));
        return f(0,0,51,nums,dp);
    }
};
