class Solution {
private:
    long long dp[2001][51];
    const int mod = 1e9 + 7;
    long long memo(int idx, int prevValOne, int prevValTwo, int n, vector<int> &nums){
        if( idx == n ){
            return 1 * 1ll;
        }
        long long &ans = dp[idx][prevValOne];
        if( ans != -1 ) return ans;
        long long cnt = 0;
        for(int i = prevValOne;nums[idx] - i >= 0; i++){
            if(nums[idx] - i <= prevValTwo){
                cnt += memo(idx + 1, i, nums[idx] - i, n, nums);
                cnt %= mod;
            }
        }
        return ans = cnt;
    }

public:
    int countOfPairs(vector<int>& nums) {
        vector<int> t, s;
        memset(dp, -1, sizeof(dp));
        long long cntPairs = memo(0, 0, 1e9, nums.size(), nums);
        return cntPairs;
    }
};
